<?php
session_start(); 
require_once '../includes/db_connect.php';
require_once '../includes/tracker.php';

if (!isset($_SESSION['user_id']) || $_SESSION['user_role'] !== 'patient') { header('Location: ../index.php'); exit(); }
$nom_patient = htmlspecialchars($_SESSION['user_nom']);
$medecins = [];
try { $stmt_medecins = $pdo->query("SELECT id_personnel, nom, prenom, specialite FROM personnel WHERE role = 'medecin' ORDER BY nom, prenom"); $medecins = $stmt_medecins->fetchAll(); } catch (PDOException $e) { die("Erreur: " . $e->getMessage()); }
$creneaux_disponibles = []; $medecin_selectionne_id = $_POST['medecin'] ?? null; $date_selectionnee = $_POST['date_rdv'] ?? null;
if (!empty($medecin_selectionne_id) && !empty($date_selectionnee)) {
    $duree_creneau = 20; $heure_debut_matin = new DateTime($date_selectionnee . ' 09:00'); $heure_fin_matin = new DateTime($date_selectionnee . ' 12:00'); $heure_debut_aprem = new DateTime($date_selectionnee . ' 14:00'); $heure_fin_aprem = new DateTime($date_selectionnee . ' 17:00');
    $tous_creneaux_theoriques = []; $current = clone $heure_debut_matin; while($current < $heure_fin_matin) { $tous_creneaux_theoriques[] = $current->format('Y-m-d H:i:s'); $current->modify('+' . $duree_creneau . ' minutes'); } $current = clone $heure_debut_aprem; while($current < $heure_fin_aprem) { $tous_creneaux_theoriques[] = $current->format('Y-m-d H:i:s'); $current->modify('+' . $duree_creneau . ' minutes'); }
    $stmt_rdv_pris = $pdo->prepare("SELECT date_heure_rdv FROM rendez_vous WHERE id_personnel_fk = ? AND DATE(date_heure_rdv) = ? AND statut = 'Confirmé'"); $stmt_rdv_pris->execute([$medecin_selectionne_id, $date_selectionnee]);
    $rdv_pris = $stmt_rdv_pris->fetchAll(PDO::FETCH_COLUMN); $creneaux_disponibles = array_diff($tous_creneaux_theoriques, $rdv_pris);
}
?>
