<?php if ($_SERVER['REQUEST_METHOD'] === 'POST'): ?><?php if (!empty($creneaux_disponibles)): ?><div class="card p-4 mt-4 shadow-sm border-0"><h4 class="mb-3">3. Choisissez un créneau pour le <?= htmlspecialchars(date('d/m/Y', strtotime($date_selectionnee))) ?></h4><div class="d-flex flex-wrap gap-2"><?php foreach ($creneaux_disponibles as $creneau): ?><a href="confirmer_rdv.php?datetime=<?= urlencode($creneau) ?>&medecin=<?= $medecin_selectionne_id ?>" class="btn btn-lg" style="background-color: var(--secondary-green); color: white;"><?= htmlspecialchars(date('H:i', strtotime($creneau))) ?></a><?php endforeach; ?></div></div><?php elseif(!empty($medecin_selectionne_id) && !empty($date_selectionnee)): ?><div class="alert alert-warning mt-4">Aucun créneau disponible.</div><?php endif; ?><?php endif; ?>
